INSERT INTO `post` VALUES (54, '佛山市南海区里水镇养狗证办理流程 办理养犬证管理服务平台电话', '<h3 class=\"ql-align-center\">佛山市南海区里水镇养<a href=\"https://www.liulangla.cn/index.php/bbs/show-860.html\" rel=\"noopener noreferrer\" target=\"_blank\" style=\"color: rgb(45, 55, 75);\">狗</a>证办理流程</h3><h3>受理条件</h3><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;1.完全民事行为能力人或在我市合法登记注册的单位；2.在本市有固定住所； 3.饲养的<a href=\"https://www.liulangla.cn/index.php/bbs/show-860.html\" rel=\"noopener noreferrer\" target=\"_blank\" style=\"color: rgb(45, 55, 75);\">犬</a>只已免疫； 4.若饲养烈性犬，须不在城市市区范围。</p><p><br></p><h3>线下办理方式</h3><p>里水镇行政服务中心民生综合窗口</p><p>办理地点：里水镇新兴路15号里水行政服务中心一楼19-20号窗口</p><p>办公电话：0757-85634072</p><p>办公时间：周一至周五（工作日）上午：8：30－12：00下午：2：00－5：30</p><p>位置指引：乘坐里02A路、里02B路、里07路，在里水电影院站下车。</p><h3>手机app办理方式</h3><h3><img src=\"https://www.liulangla.cn/public/uploads/images/20220531/1_20220531145719600bb.jpg\" alt=\"1_20220531145719600bb.jpg\"></h3><p><br></p>', '2023-06-05 22:10:20', 0, 0, 1, 1, 0, 0);
